<?php	// Improved by Crosire	if ($pageNum > 1) { 		$page = $pageNum - 1;		$prev = '<a href="'.$self.'&page='.$page.'" class="page-left"></a>';		$first = '<a href="'.$self.'&page=1" class="page-far-left"></a>';	} else { 		$prev = '';		$first = '';	}	if ($pageNum < $maxPage) { 		$page = $pageNum + 1;		$next = '<a href="'.$self.'&sort='.$sort.'&page='.$page.'" class="page-right"></a>';		$last = '<a href="'.$self.'&sort='.$sort.'&page='.$maxPage.'" class="page-far-right"></a>';	} else { 		$next = '';		$last = '';	}	$paging = '<table border="0" cellpadding="0" cellspacing="0" id="paging-table"><tr><td>'.$first.$prev.'<div id="page-info">Page <strong>'.$pageNum.'</strong> / '.$maxPage.'</div>'.$next.$last.'</td></tr></table>';?>